export function pluralize(name, count) {
  if (count === 1) {
    return name
  }
  return name + 's'
}

export function idbPromise(storeName, method, object) {
  return new Promise((resolve, reject) => {
    //open connection to the database `shop-shop` with the version of 1
    const request = window.indexedDB.open('shop-shop', 1);

    //create variables to hold reference to the database, transaction (tx), and object store
    let db, tx, store;

    // if version has changed (or if this is the first time using the database), run this method and create the three object stores
    request.onupgradeneeded = function(e) {
      const db = request.result;
      //create object store for each type of data and set "primar" key index to be the '_id' of the data
      db.createObjectStore('products', { keyPath: '_id' });
      db.createObjectStore('categories', { keyPath: '_id' });
      db.createObjectStore('cart', { keyPath: '_id' });
    };

    //handle any errors with conenecting
    request.onerror = function(e) {
      console.log('There was an error');
    };

    //on database open success
    request.onsuccess = function(e) {
      //save a reference of the daatabse to the 'db' variable
      db = request.result;
      //open a transaction do whatever we pass into 'storeName' (must match one of the object store names)
      tx = db.transaction(storeName, 'readwrite');
      //save a refgerence tot hat object store
      store = tx.objectStore(storeName);

      //if there's any errors let us know
      db.onerror = function(e) {
        console.log('error', e);
      };

      switch (method) {
        case 'put':
          store.put(object);
          resolve(object);
          break;
        case 'get':
          const all = store.getAll();
          all.onsuccess=function() {
            resolve(all.result);
          };
        break;
        case 'delete':
          store.delete(object._id);
          break;
        default:
          console.log('No valid method');
          break;
      }

      //when the transaction is complete close the connection
      tx.oncomplete = function() {
        db.close();
      };
    };


  });
}

export default function sortProducts(sortOption, products) {
  if (sortOption==='Name') {
    console.log(products.products);
    const productsArr = products.products;
    console.log(productsArr);
    productsArr.sort((a,b) => a.name.localeCompare(b.name));
    console.log(products)
  } else if (sortOption==='Price Low to High') {
    products.products.slice().sort((a, b) => a.price - b.price);
  } else if (sortOption==='Price High to Low'){
    products.products.slice().sort((a, b) => b.price - a.price);
  } else {
  }
}
